       // 第1种初始化
       var uiListview = bui.listview({
             id: "#listview",
             data: [{ "text": "置顶", "classname": "primary" }, { "text": "删除", "classname": "danger" }],
             position: "right", //默认就是右边,所以可以不用传
             callback: function (e) {
                 // this 为滑动出来的操作按钮
                 var $this = $(e.target);
                var text = $this.text();
                // bui 1.7.3 的新增做法
                 switch (text) {
                     case "删除":
                         bui.confirm("确定要删除吗", (e) => {
                             //this 是指点击的按钮
                             var text2 = $(e.target).text();
                             if (text2 == "确定") {
                                 // 执行删除整行操作
                                 this.remove();
                             }
                         })
                         break;
                     case "置顶":
                        $this.text("取消置顶");
                         this.pin();
                         break;
                     case "取消置顶":
                         $this.text("置顶");
                        this.unpin();
                         break;
                 }
                // 1.7.3 以下
                 // if (text == '删除') {
                 //     this.remove()
                 //     bui.confirm("确定要删除吗", function (e) {
                 //         //this 是指点击的按钮
                 //         var text2 = $(e.target).text();
                 //         if (text2 == "确定") {
                 //             // 执行删除整行操作
                 //             $this.parents("li").fadeOut(300, function () {
                 //                 $(this).remove();
                 //             });
                 //         }
                 //     })
                 // }
                // 不管做什么操作,先关闭按钮,不然会导致第一次点击无效.
                 this.close();
             }
         });
    // 示例结构2: 禁止渲染

         <ul id="listview" class="bui-listview">
           <!-- 设置status属性可以避免渲染菜单 -->
           <li status="1">
